mapper.select:
SELECT C.CHAL_ID AS CHALID, C.SUB_CLASS AS SUBCLASS, C.FREQUENCY AS FREQUENCY, C.MEMBER_ID AS MEMBERID, C.CHAL_NAME AS CHALNAME
FROM CHALLENGES C JOIN MEMBERS M
ON C.MEMBER_ID = M.MEMBER_ID;
WHERE C.MEMBER_ID = #{memberId} 
AND CURRENT_DATE BETWEEN C.START_DATE AND C.END_DATE
AND M.RIGHT = '일반회원';

SELECT C.CHAL_ID AS CHALID, C.SUB_CLASS AS SUBCLASS, C.FREQUENCY AS FREQUENCY, C.MEMBER_ID AS MEMBERID, C.CHAL_NAME AS CHALNAME
FROM CHALLENGES C JOIN MEMBERS M
ON C.MEMBER_ID = M.MEMBER_ID;
WHERE C.MEMBER_ID = #{memberId} 
AND CURRENT_DATE > C.START_DATE
AND M.RIGHT = '일반회원';

SELECT C.CHAL_ID AS CHALID, C.SUB_CLASS AS SUBCLASS, C.FREQUENCY AS FREQUENCY, C.MEMBER_ID AS MEMBERID, C.CHAL_NAME AS CHALNAME
FROM CHALLENGES C JOIN MEMBERS M
ON C.MEMBER_ID = M.MEMBER_ID;
WHERE C.MEMBER_ID = #{memberId} 
AND CURRENT_DATE > C.END_DATE
AND M.RIGHT = '일반회원';

mapper.update:
UPDATE CHALLENGES
SET DONATION_FEE = #{donationFee}
WHERE CHAL_ID=#{chalId}
