mapper.select:
SELECT * FROM (SELECT ROWNUM RN, A.* FROM( SELECT C.CHAL_ID, C.CLASSES, C.SUB_CLASS, C.CHAL_NAME, C.START_DATE, C.END_DATE, M.FILE_ROUTE, M.MEDIA_NAME, C.FREQUENCY FROM CHALLENGES C JOIN MEDIA M ON C.CHAL_ID = M.GROUP_ID WHERE M.SUB_GROUP = '썸네일'
ORDER BY CHAL_ID DESC
)A WHERE ROWNUM <= #{last} ) WHERE RN >= #{first}

		 SELECT * FROM (SELECT ROWNUM RN, A.* FROM( SELECT C.CHAL_ID,
		C.CLASSES, C.SUB_CLASS, C.CHAL_NAME, C.START_DATE, C.END_DATE,
		M.FILE_ROUTE, M.MEDIA_NAME, C.FREQUENCY
		FROM CHALLENGES C JOIN MEDIA M
		ON C.CHAL_ID = M.GROUP_ID
        WHERE M.SUB_GROUP = '썸네일'
        AND C.NGO_NAME=#{ngoName}
		ORDER BY CHAL_ID DESC

				)A WHERE ROWNUM <= #{last} ) WHERE RN >= #{first}

		 SELECT * FROM (SELECT ROWNUM RN, A.* FROM( SELECT C.CHAL_ID,
		C.CLASSES, C.SUB_CLASS, C.CHAL_NAME, C.START_DATE, C.END_DATE,
		M.FILE_ROUTE, M.MEDIA_NAME, C.FREQUENCY
		FROM CHALLENGES C JOIN MEDIA M
		ON C.CHAL_ID = M.GROUP_ID
        WHERE C.SUB_CLASS = #{subClass}
		ORDER BY CHAL_ID DESC

				)A WHERE ROWNUM <= #{last} ) WHERE RN >= #{first}

SELECT * FROM (SELECT ROWNUM RN, A.* FROM( SELECT C.CHAL_ID, C.CLASSES, C.SUB_CLASS, C.CHAL_NAME, C.START_DATE, C.END_DATE, M.FILE_ROUTE, M.MEDIA_NAME, C.FREQUENCY FROM CHALLENGES C JOIN MEDIA M ON C.CHAL_ID = M.GROUP_ID WHERE M.SUB_GROUP = '썸네일'
AND C.START_DATE > CURRENT_DATE ORDER BY CHAL_ID DESC
)A WHERE ROWNUM <= #{last} ) WHERE RN >= #{first}

SELECT * FROM (SELECT ROWNUM RN, A.* FROM( SELECT C.CHAL_ID, C.CLASSES, C.SUB_CLASS, C.CHAL_NAME, C.START_DATE, C.END_DATE, M.FILE_ROUTE, M.MEDIA_NAME, C.FREQUENCY FROM CHALLENGES C JOIN MEDIA M ON C.CHAL_ID = M.GROUP_ID WHERE M.SUB_GROUP = '썸네일' AND CURRENT_DATE > C.END_DATE
ORDER BY CHAL_ID DESC
)A WHERE ROWNUM <= #{last} ) WHERE RN >= #{first}

mapper.update:
UPDATE CHALLENGES
SET DONATION_FEE = #{donationFee}
WHERE CHAL_ID=#{chalId}

UPDATE CHALLENGES
SET RECEIPT = #{receipt}
WHERE CHAL_ID = #{chalId}

UPDATE CHALLENGES
SET SHAREPOINT = #{sharepoint}
WHERE CHAL_ID = #{chalId}

UPDATE MEMBERS
SET POINT = POINT + #{thatPoint}
WHERE MEMBER_ID IN (SELECT PARTICIPANT_ID
					FROM PARTICIPANTS
					WHERE TEAM = #{team}
					AND CHAL_ID = #{chalId})

UPDATE MEMBERS
SET POINT = POINT + #{thatPoint}
WHERE MEMBER_ID = #{participantId}

mapper.select:
    	SELECT TOTALDAYS(START_DATE,END_DATE,FREQUENCY) TOTAL,
        CHAL_ID, CLASSES, SUB_CLASS, CHAL_NAME,NGO_NAME,
        TO_CHAR(START_DATE,'YYYY/MM/DD')||'~'||TO_CHAR(END_DATE,'YYYY/MM/DD') TERM, MEMBER_ID, TEAM_FEE, DONATION_FEE, RECEIPT, SHAREPOINT
        FROM CHALLENGES
        WHERE CURRENT_DATE > END_DATE
        AND SUB_CLASS = #{subClass}

				AND RECEIPT = #{receipt}


				AND SHAREPOINT = #{sharepoint}


				 AND START_DATE >= #{startDate} AND END_DATE <= #{endDate}

		ORDER BY END_DATE DESC
