mapper.select:
SELECT *
FROM PARTICIPANTS
WHERE CHAL_ID = #{chalId}
AND PARTICIPANT_ID = #{participantId}

SELECT count(*) as cnt
FROM PARTICIPANTS
WHERE CHAL_ID = #{chalId}
AND PARTICIPANT_ID = #{participantId}

mapper.update:
UPDATE PAYMENTS
SET STATUS = '환불'
WHERE PAYMENT_ID = #{paymentId}

mapper.delete:
DELETE PARTICIPANTS
WHERE CHAL_ID = #{chalId}
AND PARTICIPANT_ID = #{participantId}
