mapper.select:
SELECT CASE WHEN COUNT(*) != 0 THEN 'T' ELSE 'F' END AS SUB_COUNT
FROM LIKES
WHERE GROUP_ID= #{groupId} 
AND MEMBER_ID = #{memberId}

mapper.delete:
DELETE FROM LIKES
 WHERE GROUP_ID = #{groupId}
   AND MEMBER_ID = #{memberId}

mapper.update:
update blogs set blog_name = #{blogName},
               introduction = #{introduction} 
 where blog_id = #{blogId}
