mapper.select:
 		select case when count(*) != 0 then 'Y' else 'F' end as like_count
		from likes
		where member_id = #{memberId}
		and group_id = #{groupId}

mapper.delete:
 		DELETE FROM LIKES
		WHERE
		GROUP_ID=#{groupId}
		AND MEMBER_ID=#{memberId}

delete from blog_writes
where blog_id = #{blogId}
and write_id = #{wirteId}
