mapper.select:
SELECT * FROM ( SELECT ROWNUM RN, A.* FROM ( SELECT C.CLASS_ID, C.CLASS_NAME, C.PRICE, C.CLASS_TIME, C.CLASS_GROUP, NVL(TRUNC(AVG(R.REVIEW_STAR),1),0) AS STAR, M.FILE_NAME, M.FILE_ROUTE, M.MEDIA_NAME FROM CLASS_INFO C, REVIEWS R, MEDIA M, CLASSES L WHERE C.CLASS_ID = R.GROUP_ID(+) AND C.CLASS_ID = M.GROUP_ID AND C.CLASS_ID = L.CLASS_ID(+) AND M.SUB_GROUP = '대표이미지'
GROUP BY C.CLASS_ID, C.CLASS_NAME, C.PRICE, C.CLASS_TIME, C.CLASS_GROUP, M.FILE_NAME, M.FILE_ROUTE, M.MEDIA_NAME
ORDER BY C.CLASS_ID ORDER BY STAR DESC ORDER BY C.PRICE DESC ORDER BY C.PRICE
)A WHERE ROWNUM <= #{last} ) WHERE RN >= #{first}

SELECT CASE WHEN COUNT(*) != 0 THEN 'Y' ELSE 'F' END AS
LIKE_CK
FROM LIKES
WHERE MEMBER_ID = #{memberId}
AND GROUP_ID =
#{groupId}

mapper.delete:
DELETE FROM LIKES
WHERE
GROUP_ID=#{groupId}
AND MEMBER_ID=#{memberId}
