mapper.select:
SELECT * FROM ( SELECT ROWNUM RN, A.* FROM ( SELECT C.CLASS_ID, C.CLASS_NAME, C.PRICE, C.CLASS_DATE, NVL(TRUNC(AVG(R.REVIEW_STAR),1),0) AS STAR FROM CLASS_INFO C, REVIEWS R, CLASSES L , (SELECT * FROM MEMBERS WHERE STATUS != 'false'
AND BANDATE < SYSDATE ) M WHERE C.CLASS_ID = R.GROUP_ID(+) AND C.MEMBER_ID = M.MEMBER_ID AND C.CLASS_ID = L.CLASS_ID(+)
GROUP BY C.CLASS_ID, C.CLASS_NAME, C.PRICE, C.CLASS_DATE
ORDER BY C.CLASS_DATE DESC ORDER BY STAR DESC ORDER BY C.PRICE DESC ORDER BY C.PRICE
)A WHERE ROWNUM <= #{last} ) WHERE RN >= #{first}

SELECT COUNT(*) AS TOTAL_RECORD FROM(SELECT DISTINCT C.CLASS_ID FROM CLASS_INFO C, CLASSES L, (SELECT * FROM MEMBERS WHERE STATUS != 'false'
AND BANDATE < SYSDATE ) M WHERE C.CLASS_ID = L.CLASS_ID(+) AND C.MEMBER_ID = M.MEMBER_ID
)

SELECT CASE WHEN COUNT(*) != 0 THEN 'Y' ELSE 'F' END AS
LIKE_CK
FROM LIKES
WHERE MEMBER_ID = #{memberId}
AND GROUP_ID =
#{groupId}

mapper.delete:
DELETE FROM LIKES
WHERE
GROUP_ID=#{groupId}
AND MEMBER_ID=#{memberId}

mapper.select:
SELECT *
FROM CLASS_INFO

WHERE CLASS_DATE > (CURRENT_DATE - 30)


	AND STATUS = #{status}



AND CLASS_DATE >= #{startDate}




AND CLASS_DATE <= #{endDate}


ORDER BY CLASS_DATE DESC
