mapper.select:
		SELECT * FROM ( SELECT ROWNUM RN, A.* FROM (
		SELECT  C.CLASS_ID,  C.NAME,  C.PRICE,  C.CLASS_TIME, C.CLASS_GROUP, NVL(TRUNC(AVG(R.REVIEW_STAR),1),0) AS star, M.FILE_NAME, M.FILE_ROUTE, M.MEDIA_NAME
		FROM CLASS_INFO C, REVIEWS R, MEDIA M
        where  C.CLASS_ID = R.GROUP_ID(+)
        and C.CLASS_ID = M.GROUP_ID
        and M.SUB_GROUP = '대표이미지'

				AND C.NAME LIKE '%' || #{name} || '%'



					AND C.PRICE >= #{minPrice}




					AND C.PRICE <= #{maxPrice}


		GROUP BY C.CLASS_ID,  C.NAME,  C.PRICE,  C.CLASS_TIME, C.CLASS_GROUP, M.FILE_NAME, M.FILE_ROUTE, M.MEDIA_NAME
		ORDER BY C.CLASS_ID

				)A WHERE ROWNUM <= #{last} ) WHERE RN >= #{first}
