mapper.select:
SELECT * FROM ( SELECT ROWNUM RN, A.* FROM (
SELECT *
FROM REVIEWS
WHERE GROUP_ID = #{groupId}

	 ORDER BY REVIEW_DATE DESC 
	 ORDER BY REVIEW_STAR DESC 
	 ORDER BY REVIEW_STAR  


		)A WHERE ROWNUM <= #{last} ) WHERE RN >= #{first}

SELECT COUNT(*) AS TOTALRECORD
FROM REVIEWS
WHERE GROUP_ID = #{groupId}
