mapper.select:
SELECT MEMBER_ID FROM MEMBERS WHERE MEMBER_ID = #{memberId}
UNION ALL
SELECT MEMBER_ID FROM APPLY_MEMBERS WHERE MEMBER_ID = #{memberId}

SELECT
COUNT(*) FROM MEMBERS
WHERE EMAIL = #{email}

SELECT RPAD(SUBSTR(MEMBER_ID,1,3),LENGTH(MEMBER_ID),'*')
AS MEMBER_ID FROM MEMBERS
WHERE EMAIL = #{email}

mapper.update:
UPDATE MEMBERS
SET PASSWORD =
#{password}
WHERE EMAIL = #{email}

mapper.select:
SELECT *
FROM MEMBERS


		AND STATUS=#{vo.status}


		AND ROLE LIKE '%'||#{vo.role}


		AND TO_CHAR(SIGNUPDATE,'yyyy-MM-dd')>=#{startDate} AND TO_CHAR(SIGNUPDATE,'yyyy-MM-dd')<=#{endDate}


ORDER BY SIGNUPDATE DESC

SELECT M.FILE_ROUTE 
FROM APPLY_MEMBERS A,MEDIA M 
WHERE A.APPLY_ID=M.GROUP_ID AND A.MEMBER_ID=#{memberId}
